import './App.css';
import io from "socket.io-client";
import Board from './Components/Board';
import JoinGame from './Components/JoinGame';
import { Route, Routes, useNavigate } from 'react-router-dom';
import { useState } from 'react';

const newSocket = io(`ws://${window.location.hostname}:8080`);

function App() {

  const navigate = useNavigate();
  const [username, setUsername] = useState("")

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={
          <div>
            <h2>Enter username</h2>
            <input placeholder="Username" value={username} onChange={ async (event) => {
              setUsername(event.target.value);}}></input>
            <button onClick={() => {navigate("rooms")}}>Continue</button>
          </div>
        } />
        <Route path='/rooms' element={<JoinGame socket={newSocket} username={username}/>} />
        <Route path="/game" element={<Board socket={newSocket} username={username}/>} />
      </Routes>
    </div>
  );
}

export default App;
