import './App.css';
import io from "socket.io-client";
import Board from './Components/Board';
import JoinGame from './Components/JoinGame';
import { Route, Routes } from 'react-router-dom';
import { useState } from 'react';

const newSocket = io(`ws://${window.location.hostname}:8080`);

function App() {

  let playerID = null;

  newSocket.on("connect", () => {
    console.log("playerID ", newSocket.id);
    playerID = newSocket.id;
  })

  return (
    <div className="App">
      <Routes>
        <Route path='/' element={<JoinGame socket={newSocket}/>} />
        <Route path="/game" element={<Board socket={newSocket} playerID={playerID}/>} />
      </Routes>
    </div>
  );
}

export default App;
