import React, { useState } from 'react';

function Square({chooseSquare, val}) {
    const [col, setCol] = useState("white")
    return( 
        <div className='square' style={{backgroundColor: col}} onClick={() => {
            chooseSquare(); 
            if (col === "white") {
                setCol(val === "X" ? "green" : "red")}}}>
            {val}    
        </div>
    )
}

export default Square;
