import React, { useState } from 'react';

function Square({chooseSquare, val}) {
    const [col, setCol] = useState("white")
    if (val === "") {
        setCol("white");
    } else if (val === "X") {
        setCol("green");
    } else {
        setCol("red")
    }
    return( 
        <div className='square' style={{backgroundColor: val === "" ? "white" : (val === "X" ? "green" : "red")}} onClick={chooseSquare}>
            {val}    
        </div>
    )
}

export default Square;
