import React, {useEffect, useState} from "react";
import Square from "./Square";

function Board({socket}) {

    const [board, setBoard] = useState(["", "", "", "", "", "", "", "", ""]);
    const [player, setPlayer] = useState("X");
    const [turn, setTurn] = useState("X");
    const [winner, setWinner] = useState("None");
    

    const chooseSquare = async (square) => {
        if (turn === player && board[square] === "") {
            setTurn(player === "X" ? "O" : "X");
            await socket.emit("sendMove", {square, "id": socket.id, player})
            setBoard(board.map((val, index) => {
                if (index === square && val === "") {
                    return player;
                }
                return val;
            }))
            checkWin();
        }
    }

    const winningPatterns = [[0,1,2], [3,4,5], [6,7,8], [0,3,6], [1,4,6], [2,5,8], [0,4,8], [2,4,6]]

    const checkWin = () => {
        for (const pattern of winningPatterns) {
            if (board[pattern[0]] === "") {
                continue;
            }
            if (board[pattern[0]] === board[pattern[1]] && board[pattern[0]] === board[pattern[2]]) {
                setWinner(player);
                return;
            }
        }
        return;
    }

    socket.on("updateBoard", (message) => {
        setBoard(message);
    })


    socket.on("sendMove", (move) => {
        console.log("player id", socket.id);
        if (move.id !== socket.id) {
            const currentPlayer = move.player === "X" ? "O" : "X";
            setPlayer(currentPlayer);
            setTurn(currentPlayer);
            setBoard(board.map((val, index) => {
                if (index === move.square && val === "") {
                    return move.player;
                }
                return val;
            }))
        }
    })

    return(
        <div className="board">
            {winner ==="None" ? <div>
                <div className="row" aria-label="row 1" >
                    <Square val={board[0]} chooseSquare={() => {chooseSquare(0)}}/>
                    <Square val={board[1]} chooseSquare={() => {chooseSquare(1)}}/>
                    <Square val={board[2]} chooseSquare={() => {chooseSquare(2)}}/>
                </div>
                <div className="row" aria-label="row 1">
                    <Square val={board[3]} chooseSquare={() => {chooseSquare(3)}}/>
                    <Square val={board[4]} chooseSquare={() => {chooseSquare(4)}}/>
                    <Square val={board[5]} chooseSquare={() => {chooseSquare(5)}}/>
                </div>
                <div className="row" aria-label="row 1">
                    <Square val={board[6]} chooseSquare={() => {chooseSquare(6)}}/>
                    <Square val={board[7]} chooseSquare={() => {chooseSquare(7)}}/>
                    <Square val={board[8]} chooseSquare={() => {chooseSquare(8)}}/>
                </div>
            </div> : <div></div>}
            <div>Game over, {winner} wins</div>
        </div>
    )
}

export default Board;
