import React, { useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";

function JoinGame({socket}) {

    const [username, setUsername] = useState("");

    //Create array of rooms with button to join
    const [rooms, setRooms] = useState([])

    const createRoom = () => {
        setUsername("");
        socket.emit("createRoom", `${username}`);
        socket.on("allRooms", (message) => {
            setRooms(message);
            console.log(message);
        })
    }

    const joinRoom = (roomName) => {
        socket.emit("joinRoom", `${roomName}`)
        useNavigate("game")
    }

    return(
        <div>
            <div className="joinGame">
                <h4>Create Game</h4>
                <input placeholder="Username" value={username} onChange={(event) => {
                setUsername(event.target.value);}}></input>
                <button onClick={createRoom}>Create Session</button>
            </div>
            <div className="rooms">
                {rooms ? rooms.map((r) => {
                    return(
                        <ul key={r}>{r} <button onClick={() => {joinRoom(r)}}>Join room</button></ul>
                    );
                }) : []}
            </div>
        </div>
    )
}

export default JoinGame;
